    flows.forEach( (flow,ix) => {
      let amPolicyName = 'AM-PreparedQuery-' + (ix+1);
      let d = path.dirname(sourceFilename),
          fqAssignMessagePolicyFileName = path.resolve(d, '..', 'policies', amPolicyName + '.xml');
      let avContent1 =
